import re

def PatternCount(Text, Pattern):
	count = 0
	i = 0
	k = len(Pattern) # kmer
	while i <= len(Text) - k:
		curr =  Text[i: i + k]
		if curr  == Pattern:
			count += 1
		i += 1
	return count
	

pattern = "AGGCTTGAG"
text = ""

count = PatternCount(text, pattern)
print("Pattern found: {}".format(count)) # => 31

